{
    "schemaVersion": "1.46.0",
    "class": "Device",
    "async": true,
    "label": "my BIG-IP declaration for declarative onboarding",
    "Common": {
        "class": "Tenant",
        "mySystem": {
            "class": "System",
            "hostname": "{{BIGIP1_HOSTNAME}}",
            "cliInactivityTimeout": 1200,
            "consoleInactivityTimeout": 1200,
            "autoPhonehome": false
        },
        "myDns": {
            "class": "DNS",
            "nameServers": [
                "{{DNS_NAME_SERVER}}"
            ]
        },
        "myNtp": {
            "class": "NTP",
            "servers": [
                "{{NTP_SERVER}}"
            ],
            "timezone": "UTC"
        },
        "guestUser": {
            "class": "User",
            "userType": "regular",
            "password": "{{ADMIN_PASSWORD}}",
            "partitionAccess": {
                "Common": {
                    "role": "guest"
                }
            }
        },
        "myProvisioning": {
            "class": "Provision",
            "ltm": "nominal",
            "avr": "nominal"
        },
        "inside1-self-ip": {
            "class": "SelfIp",
            "address": "{{BIGIP1_INSIDE_SELF_IP}}",
            "vlan": "{{INSIDE_VLAN}}",
            "allowService": "default",
            "trafficGroup": "traffic-group-local-only"
        },
        "inside1-floating-self-ip": {
            "class": "SelfIp",
            "address": "{{INSIDE_FLOATING_SELF_IP}}",
            "vlan": "{{INSIDE_VLAN}}",
            "allowService": "default",
            "trafficGroup": "traffic-group-1"
        },
        "outside1-self-ip": {
            "class": "SelfIp",
            "address": "{{BIGIP1_OUTSIDE_SELF_IP}}",
            "vlan": "{{OUTSIDE_VLAN}}",
            "allowService": "none",
            "trafficGroup": "traffic-group-local-only"
        },
        "outside1-floating-self-ip": {
            "class": "SelfIp",
            "address": "{{OUTSIDE_FLOATING_SELF_IP}}",
            "vlan": "{{OUTSIDE_VLAN}}",
            "allowService": "default",
            "trafficGroup": "traffic-group-1"
        },
        "configsync": {
            "class": "ConfigSync",
            "configsyncIp": "/Common/inside1-self-ip/address"
        },
        "failoverAddress": {
            "class": "FailoverUnicast",
            "address": "/Common/inside1-self-ip/address"
        },
        "failoverGroup": {
            "class": "DeviceGroup",
            "type": "sync-failover",
            "members": [
                "{{BIGIP1_HOSTNAME}}",
                "{{BIGIP2_HOSTNAME}}"
            ],
            "owner": "/Common/failoverGroup/members/0",
            "autoSync": true,
            "saveOnAutoSync": false,
            "networkFailover": true,
            "fullLoadOnSync": false,
            "asmSync": false
        },
        "traffic-group-1": {
            "class": "TrafficGroup",
            "autoFailbackEnabled": false,
            "autoFailbackTime": 50,
            "failoverMethod": "ha-order",
            "haLoadFactor": 1
        },
        "trust": {
            "class": "DeviceTrust",
            "localUsername": "{{ADMIN_USERNAME}}",
            "localPassword": "{{ADMIN_PASSWORD}}",
            "remoteHost": "{{BIGIP2}}",
            "remoteUsername": "{{ADMIN_USERNAME}}",
            "remotePassword": "{{ADMIN_PASSWORD}}"
        },
        "dbvars": {
            "class": "DbVariables",
            "ui.advisory.enabled": true,
            "ui.advisory.color": "green",
            "ui.advisory.text": "{{BIGIP1_HOSTNAME}}",
            "provision.restjavad.extramb": 2048
        }
    }
}

