{
    "class": "AS3",
    "action": "deploy",
    "persist": true,
    "declaration": {
        "class": "ADC",
        "schemaVersion": "3.53.0",
        "id": "123abc",
        "label": "HTTPS Example",
        "remark": "HTTPS with test web server iRule",
        "Common": {
            "class": "Tenant",
            "Shared": {
                "class": "Application",
                "template": "shared",
                "WebServer": {
                    "remark": "Test Web Server",
                    "class": "iRule",
                    "iRule": {
                        "base64": ""
                    }
                }
            }
        },
        "Tenant_Acme_1": {
            "class": "Tenant",
            "acme_https_app_1": {
                "class": "Application",                
                "acme_https_vs_1": {
                    "class": "Service_HTTPS",
                    "iRules": ["/Common/Shared/WebServer"],
                    "virtualAddresses": [
                        "10.91.20.101"
                    ],
                    "serverTLS": {
                        "bigip": "/Common/clientssl"
                    }
                }
            }
        }
    }
}

{
    "class": "AS3",
    "action": "deploy",
    "persist": true,
    "declaration": {
        "class": "ADC",
        "schemaVersion": "3.53.0",
        "id": "123abc",
        "label": "HTTPS Example",
        "remark": "HTTPS with test pool member nodes",
        "Tenant_Acme_2": {
            "class": "Tenant",
            "acme_https_app_2": {
                "class": "Application",                
                "acme_https_vs_2": {
                    "class": "Service_HTTPS",
                    "virtualAddresses": [
                        "10.91.20.102"
                    ],
                    "pool": "acme_http_pool",
                    "serverTLS": {
                        "bigip": "/Common/clientssl"
                    }
                },
                "acme_http_pool": {
                    "class": "Pool",
                    "loadBalancingMode": "round-robin",
                    "monitors": [
                        "http"
                    ],
                    "members": [
                        {
                            "servicePort": 8080,
                            "shareNodes": true,
                            "serverAddresses": [
                                "10.192.10.201",
                                "10.192.10.202"
                            ]
                        }
                    ]
                }
            }
        }
    }
}

